	   )

INSERT INTO BM_FOOD_OPTION 

		SELECT	GET_OPTION_SEQ()
				,#{item.foodId }
				,#{item.optionName }
				,#{item.optionPrice } 
		FROM	DUAL

mapper.update:
DECLARE BEGIN
	UPDATE	BM_FOOD 
	SET		STORE_ID = #{food.storeId }
		    ,FOOD_NAME = #{food.foodName }
		    ,FOOD_PRICE = #{food.foodPrice }
		    ,FOOD_DEC = #{food.foodDec }
		    ,FOOD_IMG = #{food.foodImg }
		    ,FOOD_THUMB = #{food.foodThumb }
	WHERE	ID = #{food.id };


		DELETE BM_FOOD_OPTION WHERE FOOD_ID = #{food.id } AND ID NOT IN

		${item.optionId }




			INSERT INTO	BM_FOOD_OPTION
			VALUES		(GET_OPTION_SEQ()
						,#{food.id }
						,#{item.optionName }
						,#{item.optionPrice }) 



			UPDATE	BM_FOOD_OPTION 
			SET		OPTION_NAME = #{item.optionName }
					,OPTION_PRICE = #{item.optionPrice }
			WHERE	FOOD_ID = #{food.id }
			AND		ID = #{item.optionId }		




END;

UPDATE BM_REVIEW SET
	BOSS_COMMENT = #{bossComment }
WHERE
	ORDER_NUM = #{orderNum }
AND
	STORE_ID = #{storeId }

UPDATE BM_ORDER_NON_USER SET
UPDATE BM_ORDER_USER SET
DELEVERY_STATUS = '의뢰 진행중(약 ${time}시간 후에 도착)' WHERE ORDER_NUM = #{orderNum }

UPDATE BM_ORDER_NON_USER SET
UPDATE BM_ORDER_USER SET
DELEVERY_STATUS = '의뢰 취소(${cancleReason })' WHERE ORDER_NUM = #{orderNum }

UPDATE BM_ORDER_NON_USER SET
UPDATE BM_ORDER_USER SET
DELEVERY_STATUS = '배달 완료' WHERE ORDER_NUM = #{orderNum }

mapper.select:
WITH T_ORDER AS ( SELECT * FROM ( SELECT ORDER_NUM, STORE_ID, ORDER_DATE, TOTAL_PRICE, DELEVERY_STATUS FROM BM_ORDER_USER UNION ALL SELECT ORDER_NUM, STORE_ID, ORDER_DATE, TOTAL_PRICE, DELEVERY_STATUS FROM BM_ORDER_NON_USER ) WHERE STORE_ID = #{storeId } AND DELEVERY_STATUS = '배달 완료'
AND TO_CHAR(ORDER_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD')
AND TO_CHAR(ORDER_DATE, 'YYYY-MM-DD') = #{date }
), T_DETAIL AS ( SELECT ORDER_NUM, LISTAGG(FOOD_INFO, '/') FOOD_INFO FROM BM_ORDER_DETAIL_USER N GROUP BY ORDER_NUM UNION ALL SELECT ORDER_NUM, LISTAGG(FOOD_INFO, '/') FOOD_INFO FROM BM_ORDER_DETAIL_NON_USER N GROUP BY ORDER_NUM ) SELECT TOTAL_PRICE ,FOOD_INFO FROM T_ORDER O LEFT JOIN T_DETAIL D ON O.ORDER_NUM = D.ORDER_NUM
